/**
 * Пример
 *
 * Время представлено строкой вида "11:34:45", содержащей часы, минуты и секунды, разделённые двоеточием.
 * Разобрать эту строку и рассчитать количество секунд, прошедшее с начала дня.
 */
fun timeStrToSeconds(str: String): Int {
    val parts = str.split(":")
    var result = 0
    for (part in parts) {
        val number = part.toInt()
        result = result * 60 + number
    }
    return result
}

/**
 * Пример
 *
 * Дано число n от 0 до 99.
 * Вернуть его же в виде двухсимвольной строки, от "00" до "99"
 */
fun twoDigitStr(n: Int) = if (n in 0..9) "0$n" else "$n"

/**
 * Пример
 *
 * Дано seconds -- время в секундах, прошедшее с начала дня.
 * Вернуть текущее время в виде строки в формате "ЧЧ:ММ:СС".
 */
fun timeSecondsToStr(seconds: Int): String {
    val hour = seconds / 3600
    val minute = (seconds % 3600) / 60
    val second = seconds % 60
    return String.format("%02d:%02d:%02d", hour, minute, second)
}

//високосный ли год
fun isLeapYear(year: Int): Boolean {
    if (year % 400 == 0) return true
    if (year % 100 == 0) return false //!!!
    if (year % 4 == 0) return true
    return false
}

//Проверка на существование даты
fun correctDate(day: Int, month: Int, year: Int): Boolean {
    if (month !in 1..12) return false
    if (month == 2 && day in 1..28) return true

    if (month == 2 && day == 29 && isLeapYear(year))
        return true

    if (day in 1..when (month) {
        1, 3, 5, 7, 8, 10, 12 -> 31
        else -> 30
    }) return true

    return false
}

fun stringToMonth(month: String): Int = when (month) {
    "января"    -> 1
    "февраля"   -> 2
    "марта"     -> 3
    "апреля"    -> 4
    "мая"       -> 5
    "июня"      -> 6
    "июля"      -> 7
    "августа"   -> 8
    "сентября"  -> 9
    "октября"   -> 10
    "ноября"    -> 11
    "декабря"   -> 12
    else -> throw Exception("bad month $month")
}

/**
 * Средняя
 *
 * Дата представлена строкой вида "15 июля 2016".
 * Перевести её в цифровой формат "15.07.2016".
 * День и месяц всегда представлять двумя цифрами, например: 03.04.2011.
 * При неверном формате входной строки вернуть пустую строку
 */
fun dateStrToDigit(str: String): String {
    val list = str.split(" ")
    if (list.size != 3) return ""

    return try {
        val day = list[0].toInt()
        val month = stringToMonth(list[1])
        val year = list[2].toInt()

        if (correctDate(day, month, year))
            String.format("%02d.%02d.%d", day, month, year)
        else ""
    } catch (e: Exception) {
        ""
    }
}

fun intToMonth(month: String): String = when (month.toInt()) {
    1  -> "января"
    2  -> "февраля"
    3  -> "марта"
    4  -> "апреля"
    5  -> "мая"
    6  -> "июня"
    7  -> "июля"
    8  -> "августа"
    9  -> "сентября"
    10 -> "октября"
    11 -> "ноября"
    12 -> "декабря"
    else -> throw Exception("bad month $month")
}

/**
 * Средняя
 *
 * Дата представлена строкой вида "15.07.2016".
 * Перевести её в строковый формат вида "15 июля 2016".
 * При неверном формате входной строки вернуть пустую строку
 */
fun dateDigitToStr(digital: String): String {
    val list = digital.split(".")
    if (list.size != 3) return ""

    try {
        val day = list[0].toInt()
        val month = intToMonth(list[1])
        val year = list[2].toInt()

        if (correctDate(day, list[1].toInt(), year))
            return String.format("%d %s %d", day, month, year)
    } catch (e: Exception) {
        return ""
    }
    return ""
}


/**
 * Средняя
 *
 * Номер телефона задан строкой вида "+7 (921) 123-45-67".
 * Префикс (+7) может отсутствовать, код города (в скобках) также может отсутствовать.
 * Может присутствовать неограниченное количество пробелов и чёрточек,
 * например, номер 12 --  34- 5 -- 67 -98 тоже следует считать легальным.
 * Перевести номер в формат без скобок, пробелов и чёрточек (но с +), например,
 * "+79211234567" или "123456789" для приведённых примеров.
 * Все символы в номере, кроме цифр, пробелов и +-(), считать недопустимыми.
 * При неверном формате вернуть пустую строку
 */
fun flattenPhoneNumber(phone: String): String {
    if (!Regex("""^\+?[0-9 ()\-]+$""").matches(phone)) return ""
    return phone.replace(Regex("""[() \-]+"""), "")
}

/**
 * Средняя
 *
 * Результаты спортсмена на соревнованиях в прыжках в длину представлены строкой вида
 * "706 - % 717 % 703".
 * В строке могут присутствовать числа, черточки - и знаки процента %, разделённые пробелами;
 * число соответствует удачному прыжку, - пропущенной попытке, % заступу.
 * Прочитать строку и вернуть максимальное присутствующее в ней число (717 в примере).
 * При нарушении формата входной строки или при отсутствии в ней чисел, вернуть -1.
 */
fun bestLongJump(jumps: String): Int {
    if (!jumps.matches(Regex("""^((\d+|%+|-+) *)+$"""))) return -1
    val res = Regex("""(\d+)""").findAll(jumps)
    var max = -1

    for (el in res.iterator())
        max = Math.max(el.value.toInt(), max)

    return max
}


/**
 * Сложная
 *
 * Результаты спортсмена на соревнованиях в прыжках в высоту представлены строкой вида
 * "220 + 224 %+ 228 %- 230 + 232 %%- 234 %".
 * Здесь + соответствует удачной попытке, % неудачной, - пропущенной.
 * Высота и соответствующие ей попытки разделяются пробелом.
 * Прочитать строку и вернуть максимальную взятую высоту (230 в примере).
 * При нарушении формата входной строки вернуть -1.
 */
fun bestHighJump(jumps: String): Int {
    //jumps.matches(Regex("""^(\d+ +[%\-+]+ *)+$""")) call StackOverflowError
    val jList = jumps.split(Regex(" +"))
    val checkR = Regex("^\\d+[+\\-%]+$")
    var max = -1

    for (i in  0 until jList.lastIndex step 2) {
        val attempts = Pair(jList[i],jList[i + 1])
        if (!(attempts.first + attempts.second).matches(checkR))
            return -1
        if (attempts.second.indexOf('+') != -1)
            max = Math.max(max, attempts.first.toInt())
    }

    return max
}


/**
 * Сложная
 *
 * В строке представлено выражение вида "2 + 31 - 40 + 13",
 * использующее целые положительные числа, плюсы и минусы, разделённые пробелами.
 * Наличие двух знаков подряд "13 + + 10" или двух чисел подряд "1 2" не допускается.
 * Вернуть значение выражения (6 для примера).
 * Про нарушении формата входной строки бросить исключение IllegalArgumentException
 */
fun plusMinus(expression: String): Int {
    if (!expression.matches(Regex("""^\d+( +[+\-] +(\d+))*$""")))
        throw IllegalArgumentException("Wrong format")

    val plus = Regex("""(^\d+)|(\+ +\d+)""").findAll(expression)
    val minus = Regex("""- +\d+""").findAll(expression)
    var result = 0

    for (el in plus) {
        val tmp = Regex("""\d+""").find(el.value)
        if (tmp != null)
            result += tmp.value.toInt()
    }

    for (el in minus) {
        val tmp = Regex("""\d+""").find(el.value)
        if (tmp != null)
            result -= tmp.value.toInt()
    }

    return result
}

/**
 * Сложная
 *
 * Строка состоит из набора слов, отделённых друг от друга одним пробелом.
 * Определить, имеются ли в строке повторяющиеся слова, идущие друг за другом.
 * Слова, отличающиеся только регистром, считать совпадающими.
 * Вернуть индекс начала первого повторяющегося слова, или -1, если повторов нет.
 * Пример: "Он пошёл в в школу" => результат 9 (индекс первого 'в')
 */
fun firstDuplicateIndex(str: String): Int {
    val words = str.split(' ')

    for (i in 0 until words.lastIndex)
        if (words[i].toLowerCase() == words[i + 1].toLowerCase())
            return str.indexOf("${words[i]} ${words[i + 1]}")

    return -1
}

